//i18n => {"account.account.Text.1":"This project is in beta. Use at your own risk.","account.account.Text.2":"Connect your wallet to continue","account.account.Text.3":"Connect"}
import i18next from "i18next";
import React, { Component } from "react";
import { withStyles } from '@material-ui/core/styles';
import {
	Typography,
	Button,
} from '@material-ui/core';
import { colors } from '../../theme';

import UnlockModal from '../unlock/unlockModal.jsx';


import {
	ERROR,
	CONNECTION_CONNECTED,
	CONNECTION_DISCONNECTED,
} from '../../constants';

import Store from "../../stores";
import {CONFIGURE_RETURNED} from "../../constants/constants-extension";
const emitter = Store.emitter;
const dispatcher = Store.dispatcher;
const store = Store.store;

const styles = theme => ({
	root: {
		flex: 1,
		display: 'flex',
		flexDirection: 'column',
		background: colors.blue,
		minWidth: '100vw',
		padding: '36px 24px'
	},
	connectHeading: {
		maxWidth: '300px',
		textAlign: 'center',
		color: colors.white
	},
	connectContainer: {
		padding: '20px'
	},
	actionButton: {
		color: colors.white,
		borderColor: colors.white
	},
	notConnectedRoot: {
		flex: 1,
		display: 'flex',
		flexDirection: 'column',
		justifyContent: 'center',
		alignItems: 'center',
	},
	connectedRoot: {
		display: 'flex',
		flexDirection: 'column',
		justifyContent: 'flex-start',
		alignItems: 'flex-start',
		width: '100%'
	},
	address: {
		color: colors.white,
		width: '100%',
		paddingBottom: '24px',
		display: 'flex',
		justifyContent: 'space-between'
	},
	balances: {
		color: colors.white,
		width: '100%',
		padding: '12px'
	},
	balanceContainer: {
		display: 'flex',
		width: '100%',
		justifyContent: 'space-between'
	},
	accountHeading: {
		paddingBottom: '6px'
	},
	icon: {
		cursor: 'pointer'
	},
	disclaimer: {
		padding: '12px',
		border: '1px solid '+colors.white,
		borderRadius: '0.75rem',
		marginBottom: '24px',
		fontWeight: 1,
		color: colors.white
	}
});

class Account extends Component {

	constructor(props) {
		super();

		const account = store.getStore('account');

		this.state = {
			loading: false,
			account: account,
			assets: store.getStore('assets'),
			modalOpen: false,
		};
	}
	componentWillMount() {
		emitter.on(ERROR, this.errorReturned);
		emitter.on(CONNECTION_CONNECTED, this.connectionConnected);
		emitter.on(CONNECTION_DISCONNECTED, this.connectionDisconnected);
		emitter.on(CONFIGURE_RETURNED, this.configureReturned);
	}

	componentWillUnmount() {
		emitter.removeListener(ERROR, this.errorReturned);
		emitter.removeListener(CONNECTION_CONNECTED, this.connectionConnected);
		emitter.removeListener(CONNECTION_DISCONNECTED, this.connectionDisconnected);
		emitter.removeListener(CONFIGURE_RETURNED, this.configureReturned);
	};

  connectionConnected = () => {
  	// this.setState({ account: store.getStore('account') })
  };

  configureReturned = () => {
  	// this.props.history.push('/')
  }

  connectionDisconnected = () => {
  	this.setState({ account: store.getStore('account'), loading: false });
  }

  errorReturned = (error) => {
  	//TODO: handle errors
  };

  render() {
  	const { classes } = this.props;
  	const {
  		account,
  		modalOpen,
  	} = this.state;

  	return (
  		<div className={ classes.root }>
  			{ this.renderNotConnected() }
  			{ modalOpen && this.renderModal() }
  		</div>
  	);
  };

  renderNotConnected = () => {
  	const { classes } = this.props;
  	const { loading } = this.state;

  	return (
  		<div className={ classes.notConnectedRoot }>
  			<Typography variant={'h5'} className={ classes.disclaimer } id="account.account.Text.1" >{i18next.t('account.account.Text.1')}</Typography>
  			<div className={ classes.connectHeading }>
  				<Typography variant='h3' id="account.account.Text.2" >{i18next.t('account.account.Text.2')}</Typography>
  			</div>
  			<div className={ classes.connectContainer }>
  				<Button
  					className={ classes.actionButton }
  					variant="outlined"
  					color="primary"
  					onClick={ this.unlockClicked }
  					disabled={ loading }
  				>
  					<Typography id="account.account.Text.3" >{i18next.t('account.account.Text.3')}</Typography>
  				</Button>
  			</div>
  		</div>
  	);
  }

  renderModal = () => {
  	return (
  		<UnlockModal closeModal={ this.closeModal } modalOpen={ this.state.modalOpen } />
  	);
  }

  unlockClicked = () => {
  	this.setState({ modalOpen: true, loading: true });
  }

  closeModal = () => {
  	this.setState({ modalOpen: false, loading: false });
  }
}

export default withStyles(styles)(Account);
